INSERT INTO `levels` VALUES (1,1,'Level 1 ‚Äì Intro to Computer Hardware','Motherboards, CPUs, RAM, GPUs, storage, I/O devices, heat sinks, and power supplies.',1,1),(2,1,'Level 2 ‚Äì Data Storage & Representation','Bits, binary, hexadecimal, ASCII, encoding, and file headers.',2,2),(3,1,'Level 3 ‚Äì Logic & Data Manipulation','Boolean logic, AND, OR, NOT, NAND, NOR, XOR, encryption with XOR.',3,3),(4,1,'Level 4 ‚Äì Storing Data & Files','File systems ‚Äî FAT32, exFAT, NTFS, EXT3/EXT4.',4,4),(5,1,'Level 5 ‚Äì Cloud Computing','Cloud service models ‚Äî SaaS, IaaS, PaaS.',5,5),(6,2,'Level 1 ‚Äì Operating Systems','The kernel, processes, interrupts, BIOS/UEFI, and boot loaders.',1,1),(7,2,'Level 2 ‚Äì Virtualization','Types and uses of virtualization, hypervisors, and setting up a VM.',2,2),(8,3,'Level 1 ‚Äì Introduction to Linux','What is Linux, distributions, GUI, and the terminal.',1,1),(9,3,'Level 2 ‚Äì Linux Environment','Super user, navigation, folder structure, file permissions, environment variables.',2,2),(10,3,'Level 3 ‚Äì Linux Navigation','Tab completion, history, interrupts, clear, parameters, and man pages.',3,3),(11,3,'Level 4 ‚Äì Linux Commands','cp, mkdir, mv, rm, cat, less, find, grep, which, apropos, file, strings, wget.',4,4),(12,3,'Level 5 ‚Äì Linux Architecture','Processes, pipes, redirects, passwd files, cron, package managers, building from source, SSH.',5,5),(13,4,'Level 1 ‚Äì Search SuperPowers','How search works, Google commands, wildcards, troubleshooting, and alternative search engines.',1,1),(14,4,'Level 2 ‚Äì WWW & Web Serving','Web servers, HTTP protocol, HTML, JavaScript, PHP, cookies, and local storage.',2,2),(15,5,'Level 1 ‚Äì Networking Fundamentals I','Network types, hardware, IP addresses, MAC addresses, packets, protocols, TCP, UDP.',1,1),(16,5,'Level 2 ‚Äì Networking Fundamentals II','IP addressing in depth, subnets, TCP/UDP in depth, protocols, Wireshark.',2,2),(17,5,'Level 3 ‚Äì Email & Protocols','Email, SMTP, POP3/IMAP/Exchange, email spoofing, SPF, DKIM.',3,3),(18,5,'Level 4 ‚Äì DNS','Domain Name System, TLDs, forward/reverse lookups, recursive/iterative, DNS records.',4,4),(19,5,'Level 5 ‚Äì Advanced Networking','ICMP, DHCP, OSI model, TCP/IP model, packet headers, ARP, DoS attacks.',5,5),(20,6,'Level 1 ‚Äì Offensive Techniques','Command Injection, XSS, CSRF, SQLi, File Inclusion, Path Traversal, Buffer Overflows, Metasploit, Social Engineering, Phishing.',1,1),(21,6,'Level 2 ‚Äì Privilege Escalation','Linux and Windows privilege escalation ‚Äî services, kernel exploits, SUID, sudo, UAC bypass, stored credentials.',2,2),(22,7,'Level 1 ‚Äì Server Fundamentals','Introduction to servers, hardware, software, and connectivity',1,1),(23,7,'Level 2 ‚Äì Web Servers','Web server architecture, configuration, and analytics',1,2),(24,7,'Level 3 ‚Äì Database Servers','Database server setup and management',1,3),(25,7,'Level 4 ‚Äì DNS Servers','DNS configuration, security, and protocols',1,4),(26,7,'Level 5 ‚Äì Log Servers','Centralized logging, SIEM, and best practices',1,5),(27,7,'Level 6 ‚Äì Email Servers','Email protocols and server configuration',1,6),(28,8,'Level 1 ‚Äì CPU & Memory','CPU architecture, registers, and memory management',1,1),(29,8,'Level 2 ‚Äì Debugging','Program tracing and debugger usage',1,2),(30,8,'Level 3 ‚Äì Advanced Storage','RAID and cloud storage systems',1,3),(31,8,'Level 4 ‚Äì Containers','Container technology and Docker',1,4),(32,9,'Level 1 ‚Äì Encryption','Cryptography fundamentals and applications',1,1),(33,9,'Level 2 ‚Äì Law & Ethics','Legal and ethical considerations in security',1,2),(34,9,'Level 3 ‚Äì Security Fundamentals','Security models and risk management',1,3),(35,9,'Level 4 ‚Äì Security Distributions','Security-focused operating systems',1,4),(36,9,'Level 5 ‚Äì Reconnaissance','Information gathering techniques',1,5),(37,9,'Level 6 ‚Äì Forensics I','Digital forensics fundamentals',1,6),(38,9,'Level 7 ‚Äì Forensics II','File system and disk forensics',1,7)